import React from 'react';

/**
 * Card-Komponente für die Anzeige von Spielkarten mit interaktiven Features
 */
function Card({ card, onClick, selected, style = {} }) {
  // Basis- und ausgewählte Styles
  const baseStyle = {
    border: '2px solid rgba(255,255,255,0.3)',
    borderRadius: '16px',
    padding: '8px',
    margin: '8px',
    cursor: onClick ? 'pointer' : 'default',
    background: 'rgba(255,255,255,0.95)',
    transition: 'all 0.3s ease',
    boxShadow: '0 8px 16px rgba(0,0,0,0.15)',
    backdropFilter: 'blur(10px)',
    ...style
  };
  const selectedStyle = {
    border: '4px solid #007bff',
    background: 'linear-gradient(135deg, #e3f2fd, #bbdefb)',
    boxShadow: '0 12px 24px rgba(0,123,255,0.3)'
  };

  // MouseOver/MouseOut Handler
  const handleMouseOver = (e) => {
    if (onClick) {
      e.currentTarget.style.transform = 'scale(1.08) translateY(-4px)';
      e.currentTarget.style.boxShadow = '0 16px 32px rgba(0,0,0,0.25)';
    }
  };
  const handleMouseOut = (e) => {
    if (onClick) {
      e.currentTarget.style.transform = selected ? 'scale(1.05)' : 'scale(1)';
      e.currentTarget.style.boxShadow = selected
        ? '0 12px 24px rgba(0,123,255,0.3)'
        : '0 8px 16px rgba(0,0,0,0.15)';
    }
  };

  // Bild-Styles
  const imgStyle = {
    width: '250px',
    height: '200px',
    objectFit: 'cover',
    borderRadius: '12px',
    display: 'block',
    filter: selected ? 'brightness(1.1) contrast(1.1)' : 'brightness(1)',
    transition: 'filter 0.3s ease'
  };

  return (
    <div
      onClick={onClick}
      style={selected ? { ...baseStyle, ...selectedStyle } : baseStyle}
      onMouseOver={handleMouseOver}
      onMouseOut={handleMouseOut}
    >
      <img
        src={`/${card.image}`}
        alt={card.title || `Card ${card.id}`}
        style={imgStyle}
        onError={(e) => {
          // Fallback-Bild falls das Kartenbild nicht geladen werden kann
          e.target.src = `https://placehold.co/250x200/cccccc/666666?text=Card+${card.id}`;
        }}
      />
    </div>
  );
}

export default Card;

import React from 'react';
import './LobbyStyle.css';

function Lobby({ players, gameId, error, onJoin, onStart, onLeave }) {
  return (
    <div className="lobby-container">
      <h2> Raum: {gameId}</h2>
      <div className="lobby-players">
        <h3> Spieler ({players.length})</h3>
        <div style={{ display: 'grid', gap: '12px', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))' }}>
          {players.map((player, idx) => (
            <div key={player.id} className="lobby-player-card">
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>
                {idx === 0 ? '' : ''}
              </div>
              <div style={{ fontWeight: 'bold', fontSize: '16px' }}>{player.name}</div>
              {idx === 0 && <div style={{ color: '#FFD700', fontSize: '12px', marginTop: '4px', fontWeight: 'bold' }}>Raumleiter</div>}
            </div>
          ))}
        </div>
      </div>
      {error && <div className="lobby-error">⚠️ {error}</div>}
      <button className="lobby-btn" onClick={onJoin}> Raum beitreten</button>
      <button className="lobby-btn" onClick={onStart} disabled={players.length < 3}> Spiel starten!</button>
      <button className="lobby-btn" onClick={onLeave}> Verlassen</button>
    </div>
  );
}

export default Lobby;

